def findMin(self, nums: List[int]) -> int:
        start = 0
        end = len(nums) - 1

        if(nums[start] <= nums[end]):
            return nums[0]

        while start <= end:
            mid = (start + end) // 2

            if(nums[mid] > nums[mid+1]):
                return nums[mid+1]

            if(nums[mid-1] > nums[mid]):
                return nums[mid]

            if(nums[mid] > nums[0]):
                start = mid + 1
            else:
                end = mid - 1

        return nums[start]

def findMin(self, a: List[int]) -> int:

        def solve(l,h):
            while l<h:
                m=(l+h)//2

                if a[m]<a[m-1]:
                    return a[m]

                elif a[m]>a[h-1]:
                    l=m+1

                elif a[m]<a[h-1]:
                    h=m

                else:

                    if len(set(a[l:m+1]))==1:
                        return min(a[m],solve(m+1,h))

                    else:
                        return min(a[m],solve(l,m))

            return a[min(l,len(a)-1)]

        return solve(0,len(a))

class MinStack:

    stackWithMinElements = collections.namedtuple("stackWithMinElements", ("element", "minimum"))

    def __init__(self):        
        self.stack : List[self.stackWithMinElements] = []

    def push(self, x: int) -> None:
        self.stack.append(self.stackWithMinElements(
                        x, min(x, self.getMin()) if len(self.stack)>0 else x))

    def pop(self) -> None:
        self.stack.pop()

    def top(self) -> int:
        return self.stack[-1].element

    def getMin(self) -> int:
        return self.stack[-1].minimum

def getIntersectionNode(self, headA: ListNode, headB: ListNode) -> Optional[ListNode]:
        first_set=set()
        curr=headA

        while curr:
            first_set.add(curr)
            curr=curr.next

        curr = headB
        while curr:
            if curr in first_set:
                return curr
