        ind = n-1
        while(ind>=0):

            for buy in range(2):
                if(buy):
                    profit = max(-prices[ind] + dp[ind+1][0], 0 + dp[ind+1][1])

                else:
                    profit = max(prices[ind] + dp[ind+2][1], 0 + dp[ind+1][0])

