            val = {grid[i][j] for i, j in product(range(x0, x1), range(y0, y1))}
            if len(val) == 1: return Node(val.pop(), True, None, None, None, None)
            tl = fn(x0, (x0+x1)//2, y0, (y0+y1)//2)
            tr = fn(x0, (x0+x1)//2, (y0+y1)//2, y1)
