        partial_sum = 0

		# table is a dictionary
		# key : partial sum value
		# value : the left-most index who has the partial sum value

        table = { 0: -1}

        max_length = 0

        for idx, number in enumerate( nums ):

            # partial_sum add 1 for 1
            # partial_sum minus 1 for 0

            if number:
                partial_sum += 1
            else:
                partial_sum -= 1


