def new21Game(self, N: int, K: int, W: int) -> float:

        @lru_cache(None)
        def fn(n): 
            """Return prob of of points between K and N given current point n."""
            if N < n: return 0
            if K <= n: return 1
            if n+1 < K: return (1+W)/W*fn(n+1) - 1/W*fn(n+W+1)
