        left, right = 1, max(nums)
        while left + 1 < right:
            mid = (left + right) // 2
            div_sum =  self.get_sum(mid, nums)
            if div_sum > threshold:
                left = mid
            else:
                right = mid

        div_sum = self.get_sum(left, nums)
        if div_sum <= threshold:
