        m, n = len(mat), len(mat[0])
        mat[:] = [[0] * (n + 1)] + [[0] + row for row in mat]
        res = [[0] * n for i in range(m)]

        for i in range(1, m + 1):
            for j in range(1, n + 1):
                mat[i][j] += mat[i - 1][j] + mat[i][j - 1] - mat[i - 1][j - 1]

