            elif grid[i][j] == 2:
                if j != 0:
                    graph[(i, j)][(i, j - 1)] = 0

            elif grid[i][j] == 3:
                if i != m - 1:
                    graph[(i, j)][(i + 1, j)] = 0

            else:
                if i != 0:
                    graph[(i, j)][(i - 1, j)] = 0


        for i in range(m):
            for j in range(n):
                addEdges(i, j)

