def numberOfSets(self, n: int, k: int) -> int:

        @cache
        def fn(n, k):
            """Return number of sets."""
            if n <= k: return 0 
            if k == 0: return 1
            return 2*fn(n-1, k) + fn(n-1, k-1) - fn(n-2, k)

        return fn(n, k) % 1_000_000_007

