def countDaysTogether(self, arAl: str, lAl: str, arBo: str, lBo: str) -> int:
        months = [-1, 31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31]
        start = max(arAl, arBo)
        end = min(lAl, lBo)

        startDay = int(start[3:])
        startMonth = int(start[:2])
        endDay = int(end[3:])
        endMonth = int(end[:2])

        if start > end:
            return 0
        if startMonth == endMonth:
            return endDay-startDay+1
        elif startMonth < endMonth:
            return months[startMonth]-startDay + endDay + 1 + sum(months[m] for m in range(startMonth+1, endMonth))

def matchPlayersAndTrainers(self, players: List[int], trainers: List[int]) -> int:
        n = len(players)
        m = len(trainers)
        players.sort()
        trainers.sort()
        dp = {}
        def helper(i,j):
            if i==n or j==m:
                return 0
            if (i,j) in dp:
                return dp[(i,j)]
            if players[i]<=trainers[j]:
                dp[(i,j)] = 1+helper(i+1,j+1)
            else:
                dp[(i,j)] = helper(i,j+1)
            return dp[(i,j)]
        return helper(0,0)

def smallestSubarrays(self, nums: List[int]) -> List[int]:

        def create(m):
            t = 0
            for n in m:
                if m[n] > 0:
                    t = t | (1 << n)
            return t

        def add(a,m):
            ans = bin( a )
            s = str(ans)[2:]
            for i, b in enumerate( s[::-1]):
                if b == '1':
                    m[i] += 1

        def remove(a,m):
            ans = bin( a )
            s = str(ans)[2:]
            for i, b in enumerate( s[::-1]):
                if b == '1':
                    m[i] -= 1

        res = []


        n = defaultdict(int)
        for i in nums:
            add(i,n)


        m = defaultdict(int)
        r = 0
        c = 0

        for i,v in enumerate(nums):
            # The last check is for if nums[i] == 0, in that case we still want to add to the map
            while r < len(nums) and (create(m) != create(n) or (c==0 and nums[i] ==0)):
                add(nums[r],m)
                r+=1
                c+=1

            res.append(c)

            remove(nums[i],m)
            remove(nums[i],n)
            c-=1

        return res

